import joi from 'joi';
import queryString from 'query-string';
import {
  SendAirtimeOptions, SendAirtimePostData, SendAirtimeResponse, SendAirtimeRequest,
} from './sendAirtimeRequest.types';
import { validateJoiSchema, sendRequest } from '../../utils/misc';
